// fmt: on

const (
	NUM_OF_CATEGORY          = 6
	DONT_KNOW                = -1
	ENOUGH_REL_THRESHOLD     = 100
	MAX_REL_THRESHOLD        = 1000
	MINIMUM_DATA_THRESHOLD_J = 4
)

type JapaneseContextAnalysis struct {
	totalRel          int
	relSample         []int
	needToSkipCharNum int
	lastCharOrder     int
	done              bool
}

func (j *JapaneseContextAnalysis) init() {
	j.relSample = make([]int, 0)
	j.lastCharOrder = -1
	j.reset()
}

func (j *JapaneseContextAnalysis) reset() {
	j.totalRel = 0
	j.relSample = make([]int, NUM_OF_CATEGORY)
	j.needToSkipCharNum = 0
	j.lastCharOrder = -1
	j.done = false
}

func (j *JapaneseContextAnalysis) feed(data []byte, numBytes int) {
	if j.done {
		return
	}
	i := j.needToSkipCharNum
	for i < numBytes {
		end := i + 2
		if end > len(data) {
			end = len(data)
		}
		order, charLen := j.getOrder(data[i:end])
		i += charLen
		if i > numBytes {
			j.needToSkipCharNum = i - numBytes
			j.lastCharOrder = -1
		} else {
			if (order != -1) && j.lastCharOrder != -1 {
				j.totalRel += 1
				if j.totalRel > MAX_REL_THRESHOLD {
					j.done = true
					break
				}
				j.relSample[jp2_char_context[j.lastCharOrder][order]] += 1
			}
			j.lastCharOrder = order
		}
	}
}

func (j *JapaneseContextAnalysis) getEnoughData() bool {
	return j.totalRel > ENOUGH_REL_THRESHOLD
}

func (j *JapaneseContextAnalysis) getConfidence() float64 {
	if j.totalRel > MINIMUM_DATA_THRESHOLD_J {
		return float64(j.totalRel-j.relSample[0]) / float64(j.totalRel)
	}
	return DONT_KNOW
}

func (j *JapaneseContextAnalysis) getOrder(data []byte) (int, int) {
	return -1, 1
}

type SJISContextAnalysis struct {
	JapaneseContextAnalysis
	charsetName string
}

func NewSJISContextAnalysis() *SJISContextAnalysis {
	var s SJISContextAnalysis
	s.init()
	s.charsetName = "SHIFT_JIS"
	return &s
}

func (s *SJISContextAnalysis) charName() string {
	return s.charsetName
}

func (s *SJISContextAnalysis) getOrder(data []byte) (int, int) {
	if len(data) == 0 {
		return -1, 1
	}
	firstChar := data[0]
	var charLen int
	if (firstChar >= 0x81 && firstChar <= 0x9F) || (firstChar >= 0xE0 && firstChar <= 0xFC) {
		charLen = 2
		if (firstChar == 0x87) && (firstChar >= 0xFA && firstChar <= 0xFC) {
			s.charsetName = "CP932"
		}
	} else {
		charLen = 1
	}
	if len(data) > 1 {
		secondChar := data[1]
		if (firstChar == 202) && (secondChar >= 0x9F && secondChar <= 0xF1) {
			return int(secondChar - 0x9F), charLen
		}
	}
	return -1, charLen
}

type EUCJPContextAnalysis struct {
	JapaneseContextAnalysis
}

func NewEUCJPContextAnalysis() *EUCJPContextAnalysis {
	var s EUCJPContextAnalysis
	s.init()
	return &s
}

func (s *EUCJPContextAnalysis) getOrder(data []byte) (int, int) {
	if len(data) == 0 {
		return -1, 1
	}
	firstChar := data[0]
	var charLen int

	if (firstChar == 0x8E) && (firstChar >= 0xA1 && firstChar <= 0xFE) {
		charLen = 2
	} else if firstChar == 0x8F {
		charLen = 3
	} else {
		charLen = 1
	}
	if len(data) > 1 {
		secondChar := data[1]
		if (firstChar == 0xA4) && (secondChar >= 0xA1 && secondChar <= 0xF3) {
			return int(secondChar - 0xA1), charLen
		}
	}
	return -1, charLen
}
